import React,{useEffect, useState} from 'react'
import styled from 'styled-components';
import { BrowserRouter as Router, Route, useNavigate, Routes } from 'react-router-dom';
import Dashboard from '../Dashboard/Dashboard';

const navPaneStyle = {
  float:"left",
  display: 'flex',
  backgroundColor: 'grey',
  flexDirection: 'column',
  alignItems: 'stretch',
  width: '300px',
  maxWidth: '90%', 
  maxHeight: '90%',
  boxSizing: 'border-box',
  borderRadius:'20px',
  marginTop: '40px',
  paddingTop: '200px',
  paddingBottom: '50px',
  fontSize: '16px',
  boxShadow: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',

  '@media (min-width: 768px)': {
    flexDirection: 'row',
  },
}

const NavPaneContainer = styled.div`
  float: left;
  display: flex;
  background-color: grey;
  flex-direction: column;
  align-items: stretch;
  width: 300px;
  max-width: 90%;
  max-height: 90%;
  box-sizing: border-box;
  border-radius: 20px;
  margin-top: 40px;
  padding-top: 200px;
  padding-bottom: 50px;
  font-size: 16px;
  box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);

  @media (min-width: 768px) {
    flex-direction: row;
  }
`;

const StyledButton = styled.a`
border-radius: 0px;
border: none;
color: white;
text-align: center;
padding: 10px;
font-size: 15px;
transition: 0.3s;
outline: none;
background-color: blue;
cursor: pointer;
&:active {
  transform: scale(0.95);
}
&:hover {
  background-color: blue;
}
`;

function NavPane() {
  const [Navcontent, setNavContent] = useState('home');
  useEffect(() => {
    console.log(Navcontent);
  }, [Navcontent]);

  return (
    <Router>
      <NavPaneContainer className="nav flex-column"> 
        <NavContent />
      </NavPaneContainer>

      <Routes>
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>
    </Router>
  )
}

function NavContent() {
  const navigate = useNavigate();
  return (
    <>
      <StyledButton className="btn btn-block rounded-0" href="#" onClick={()=>navigate('/home')}>Home</StyledButton>
      <StyledButton className="btn btn-block rounded-0" href="#" onClick={() => navigate('/dashboard')}>Dashboard</StyledButton>
      <StyledButton className="btn btn-block rounded-0" href="#" onClick={()=>navigate('/products')}>Products</StyledButton>
      <StyledButton className="btn btn-block rounded-0" href="#" onClick={()=>navigate('/suppliers')}>Suppliers</StyledButton>
      <StyledButton className="btn btn-block rounded-0" href="#" onClick={()=>navigate('/orders')}>Orders</StyledButton>
    </>
  );
}


export default NavPane
