import React,{useEffect, useState} from 'react'
import styled from 'styled-components';
import { BrowserRouter as Router, Route, useNavigate, Routes } from 'react-router-dom';
import { Button, Collapse } from 'react-bootstrap';

import Dashboard from '../Dashboard/Dashboard';
const navPaneStyle = {
  float:"left",
  display: 'flex',
  backgroundColor: 'grey',
  flexDirection: 'column',
  alignItems: 'stretch',
  width: '300px',
  maxWidth: '90%', 
  maxHeight: '90%',
  boxSizing: 'border-box',
  borderRadius:'20px',
  marginTop: '40px',
  paddingTop: '200px',
  paddingBottom: '50px',
  fontSize: '16px',
  boxShadow: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
}

const StyledButton = styled.a`
border-radius: 0px;
border: none;
color: white;
text-align: center;
padding: 10px;
font-size: 15px;
transition: 0.3s;
outline: none;
background-color: blue;
cursor: pointer;
&:active {
  transform: scale(0.95);
}
&:hover {
  background-color: blue;
}
`;

function NavPane() {
  const [Navcontent, setNavContent] = useState('home');
  const [open, setOpen] = useState(true);

  const handleClick = (newNavContent) => {
    setNavContent(newNavContent);
  };

  useEffect(() => {
    console.log(Navcontent);
  }, [Navcontent]);

  return (
    <Router>
      <div style={navPaneStyle} className="nav flex-column">
        <Button
          onClick={() => setOpen(!open)}
          aria-controls="example-collapse-text"
          aria-expanded={open}
        >
          Toggle Nav
        </Button>
        <Collapse in={open}>
          <div id="example-collapse-text">
            <NavContent />
          </div>
        </Collapse>
      </div>

      <Routes>
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>
    </Router>
  );
}

function NavContent() {
  const navigate = useNavigate();
  return (
    <>
      <StyledButton className="btn btn-block rounded-0" href="#" onClick={()=>navigate('/home')}>Home</StyledButton>
      <StyledButton className="btn btn-block rounded-0" href="#" onClick={() => navigate('/dashboard')}>Dashboard</StyledButton>
      <StyledButton className="btn btn-block rounded-0" href="#" onClick={()=>navigate('/products')}>Products</StyledButton>
      <StyledButton className="btn btn-block rounded-0" href="#" onClick={()=>navigate('/suppliers')}>Suppliers</StyledButton>
      <StyledButton className="btn btn-block rounded-0" href="#" onClick={()=>navigate('/orders')}>Orders</StyledButton>
    </>
  );
}


export default NavPane
