import React,{useEffect, useState} from 'react'
import styled from 'styled-components';
import { BrowserRouter as Router, Route, useNavigate, Routes } from 'react-router-dom';
import Dashboard from '../Dashboard/Dashboard';
const navPaneStyle = {
  float:"left",
  display: 'flex',
  backgroundColor: 'grey',
  flexDirection: 'column',
  alignItems: 'stretch',
  width: '300px',
  maxWidth: '90%', 
  maxHeight: '90%',
  boxSizing: 'border-box',
  borderRadius:'20px',
  marginTop: '40px',
  paddingTop: '200px',
  paddingBottom: '50px',
  fontSize: '16px',
  boxShadow: '0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23)',
}

const StyledButton = styled.a`
border-radius: 0px;
border: none;
color: white;
text-align: center;
padding: 10px;
font-size: 15px;
transition: 0.3s;
outline: none;
background-color: blue;
cursor: pointer;
&:active {
  transform: scale(0.95);
}
&:hover {
  background-color: blue;
}
`;
function NavPane() {
  const [Navcontent, setNavContent] = useState('home');


  useEffect(() => {
    console.log(Navcontent);

  }, [Navcontent]);

  return (
    <Router>

      <div style={navPaneStyle} className="nav flex-column"> 
        <StyledButton className="btn btn-block rounded-0" href="#" onClick={()=>handleClick('home')}>Home</StyledButton>
        <StyledButton className="btn btn-block rounded-0" href="#" onClick={() => navigate('/Dashboard')}>Dashboard</StyledButton>
        <StyledButton className="btn btn-block rounded-0" href="#" onClick={()=>handleClick('products')}>Products</StyledButton>
        <StyledButton className="btn btn-block rounded-0" href="#" onClick={()=>handleClick('suppliers')}>Suppliers</StyledButton>
        <StyledButton className="btn btn-block rounded-0" href="#" onClick={()=>handleClick('orders')}>Orders</StyledButton>
      </div>

      <Routes>

        <Route path="/dashboard">
          <Dashboard />
        </Route>
      </Routes>
    </Router>
  )
}

export default NavPane
