import React, { useEffect, useState } from 'react';
import { sql } from "@vercel/postgres";

function UserTable() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    (async () => {
      try {
        const { rows } = await sql`SELECT * from users`;
        setUsers(rows);
      } catch (error) {
        console.error(error);
      }
    })();
  }, []);

  return (
    <table>
      <thead>
        <tr>
          <th>User ID</th>
          <th>First Name</th>
          <th>Middle Name</th>
          <th>Last Name</th>
          <th>Email</th>
          <th>Phone</th>
        </tr>
      </thead>
      <tbody>
        {users.map(user => (
          <tr key={user.user_id}>
            <td>{user.user_id}</td>
            <td>{user.first_name}</td>
            <td>{user.mid_name}</td>
            <td>{user.last_name}</td>
            <td>{user.email}</td>
            <td>{user.phone}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default UserTable;
